#include <openssl/bn.h>
#include <stdio.h>

#define NBITS 256

void printBN(char *msg, BIGNUM *a) {
    char *number_str = BN_bn2hex(a);
    printf("%s %s\n", msg, number_str);
    OPENSSL_free(number_str);
}

int main() {
    BN_CTX *ctx = BN_CTX_new();
    BIGNUM *e = BN_new();
    BIGNUM *n = BN_new();
    BIGNUM *dec = BN_new();
    BIGNUM *s = BN_new();

    // Replace the following values with the actual values from the OpenSSL commands
    BN_hex2bn(&n, ""); // Modulus
    BN_hex2bn(&e, "65537"); // Exponent
    BN_hex2bn(&s, "188a958903e66ddf5cfc1d68ea4a8f83d6512f8d6b44169eac63f5d26e6c84998baa8171845bed344eb0b7799229cc2d806af08e20e179a4fe034713eaf586ca59717d4"); // Signature

    BN_mod_exp(dec, s, e, n, ctx);
    printBN("Decrypted =", dec);

    return 0;
}
